import { Button, Image, TableProps } from "antd";
import { useNavigate } from "react-router-dom";
import { getImageUrl } from "src/helpers/helpers";
import {
  ENROLLMENT_STATUS,
  ENROLLMENT_STATUS_LABELS,
} from "src/utils/constants";
import { GenderCell } from "../../exams/list/useTableColumns";
import StudentType from "./types";

const useTableColumns = ({
  getCourseLabel,
}: {
  getCourseLabel: (id: number) => string;
}) => {
  const navigate = useNavigate();
  const columns: TableProps<StudentType>["columns"] = [
    {
      title: "#",
      dataIndex: "id",
      render: (text: string, record: StudentType, index: number) => index + 1,
      sorter: (a, b) => a.id - b.id,
    },
    {
      title: "Name",
      dataIndex: "name",
      render: (text: string, record: StudentType) => (
        <div className="flex flex-row gap-3 items-center">
          <Image
            src={record?.image ? getImageUrl(record?.image) : undefined}
            height={50}
            width={50}
            className="rounded-full shrink-0"
            fallback="data:image/png;base64,"
          />
          <div>{text}</div>
        </div>
      ),
    },
    {
      title: "Email",
      dataIndex: "email",
      render: (text: string) => text,
    },
    {
      title: "Contact",
      dataIndex: "phone",
      render: (text: string) => text,
    },
    {
      title: "Roll No.",
      dataIndex: "registrationId",
      render: (text: string) => text ?? "-",
    },
    {
      title: "Gender",
      dataIndex: "gender",
      render: (text: number) => <GenderCell id={text} />,
    },
    {
      title: "Course",
      dataIndex: "course",
      render: (text: number) => <div>{getCourseLabel(text)}</div>,
    },
    {
      title: "Exam Center",
      dataIndex: "course",
      render: (text: number) => <div>{getCourseLabel(text)}</div>,
    },
    {
      title: "Status",
      dataIndex: "enrollmentStatus",
      render: (text: number, record: StudentType) => {
        if (text === ENROLLMENT_STATUS.ACCEPTED) {
          return ENROLLMENT_STATUS_LABELS[ENROLLMENT_STATUS.ACCEPTED];
        }
        if (text === ENROLLMENT_STATUS.REJECTED) {
          return ENROLLMENT_STATUS_LABELS[ENROLLMENT_STATUS.REJECTED];
        }
        return (
          <div>
            {text === ENROLLMENT_STATUS.REQUESTED ? (
              <Button>
                {ENROLLMENT_STATUS_LABELS[ENROLLMENT_STATUS.REQUESTED]}
              </Button>
            ) : (
              "-"
            )}
          </div>
        );
      },
    },
  ];

  return {
    columns,
  };
};

export default useTableColumns;
